mapper.select:
select id, child_id, guardian_id, class_id, class_name, enroll_start_time, enroll_end_time, child_name, guardian_name, guardian_phone, is_pay, is_del, create_by, create_time, update_by, update_time, remark from e_enroll_record
AND child_id like CONCAT(CONCAT('%',#{enrollRecordSearch.childIdSearch}),'%')
AND create_time >= #{enrollRecordSearch.insertTimeStart}
AND create_time <= #{enrollRecordSearch.insertTimeEnd}
AND create_by = #{enrollRecordSearch.userId}
order by create_time desc
